def handle_rate_limit(func):
    def wrapper(*args, **kwargs):
        try:
            return func(*args, **kwargs)
        except RateLimitExceededException as e:
            wait_time = calculate_wait_time(e)
            time.sleep(wait_time)
            return func(*args, **kwargs)
    return wrapper
