class RuleEngine:
    def __init__(self):
        self.rules = []

    def add_rule(self, rule):
        self.rules.append(rule)

    def classify(self, repo_data):
        categories = []
        for rule in self.rules:
            if rule.matches(repo_data):
                categories.append(rule.category)
        return categories or ['uncategorized']
