# generator/builder.py
class MarkdownBuilder:
    def __init__(self, template_manager, config):
        self.template_manager = template_manager
        self.config = config

    def build_document(self, classified_repos):
        # ç»„ç»‡æ•°æ®ç»“æ„
        organized_data = self.organize_by_categories(classified_repos)

        # ç”Ÿæˆç›®å½•
        toc = self.generate_toc(organized_data)

        # æ¸²æŸ“ä¸»æ–‡æ¡£
        content = self.template_manager.render_template(
            'main.md',
            toc=toc,
            categories=organized_data,
            metadata=self.generate_metadata(classified_repos)
        )

        return content

    def organize_by_categories(self, repos):
        categories = {}
        for repo in repos:
            for category in repo.categories:
                if category not in categories:
                    categories[category] = []
                categories[category].append(repo)
        return categories
```

